def run():
    with Dispatcher() as dispatch:
        dispatch.clear(Clear.CursorDown)
        start_row = 5
        start_col = 0
        scene_count = 0
        dispatch.goto(start_col, start_row)
        scenes = SEQUENCE.split('1')
        for _ in range(5):
            for i, scene in enumerate(scenes):
                if scene[0] == '0':
                    continue
                dispatch.printf(scene)
                dispatch.goto(start_col, start_row)
                dispatch.clear(Clear.CursorDown)
                time.sleep(0.08)
                scene_count += 1
            scenes.reverse()

    dispatch.clear(Clear.NewLine)
    dispatch.clear(Clear.CursorDown)
    dispatch.flush()
    print(" ")
    print(scene_count)


if __name__ == "__main__":
    run()
